import './App.css';
import Loader from "react-loader-spinner";
import React, { useState, useEffect } from 'react'
import axios from 'axios'

const App = () => {

  let ausgabe;
  const [loading, setLoading] = useState(true); 
  if(loading){
    return(
      <Loader
      type="Puff"
      color="#00BFFF"
      height={100}
      width={100}
      timeout={3000} //3 secs
    />
    )
  }

  useEffect(() => {
    axios.get('https://hk-test-api.herokuapp.com/albums', {headers: {
      accept: 'application/json',
      'X-API-Key': 'secret'
  }})
    .then((response) => {
        console.log(response.data)
        ausgabe = response.data
    })
    .catch(e => {
        console.log('Error: ', e.response.data)
    });
  },[])

  return (
    <div className="App">
      <p>Hallo Meister</p>
      <p>{ausgabe}</p>
      <p>Test</p>
    </div>
  );
}

export default App;
