import React, { useState, useEffect } from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.css';


const App = () => {
  const [albumData, setAlbumData] = useState([])
  const [editAlbum, setEditAlbum] = useState(null)
  const [editTitleText, setEditTitleText] = useState("")
  const [editArtistText, setEditArtistText] = useState("")
  const [editReleasedYearNumber, setEditRelasedYearNumber] = useState()

  const deleteAlbumData = id => {
    const newAlbumData = albumData.filter(album => album.id !== id)
      setAlbumData(newAlbumData) 
  }

  const fetchAlbumData =  () => {
     return axios.get('https://hk-test-api.herokuapp.com/albums', {headers: {
      accept: 'application/json',
      'X-API-Key': 'secret'
  }})
    .then((response) => {
        console.log(response.data);
        return response.data
    })
    .catch(e => {
        console.log('error:', e)
    });
  }

  useEffect(() => {
    fetchAlbumData().then( albums =>{
      setAlbumData(albums)
    })
  }, [])

  const editAlbumData = (id) => {
    const updatedAlbum = [...albumData].map((album) => {
      if(album.id === id) {
        album.title = editTitleText
        album.artist = editArtistText
        album.released_in = editReleasedYearNumber
      }
      return album
    })
    setAlbumData(updatedAlbum)
    setEditTitleText("")
    setEditArtistText("")
    setEditRelasedYearNumber()
    setEditAlbum(null)
  }



  return (
    <div>
    <div className="App">
      {albumData.map( album => {
        return <div>
        <a href="#" className="list-group-item list-group-item-action flex-column align-items-start">
          <div className="d-flex w-100 justify-content-between">
            {editAlbum === album.id ? (<div class="input-group mb-3">
                          <div class="input-group-prepend">
                          <span class="input-group-text" id="basic-addon1">@</span>
                          </div>
                          <input className ="input-group-text mb-2" type='text' placeholder='Titel: ' onChange={ e => setEditTitleText(e.target.value)} value={editTitleText}/>
                          </div>) : (<h5 className="mb-1">Titel: {album.title}</h5>)}
            {editAlbum === album.id ? (<input className ="input-group-text mb-2" type='text' placeholder='Titel: ' onChange={ e => setEditTitleText(e.target.value)} value={editTitleText}/>) : (<h5 className="mb-1">Titel: {album.title}</h5>)}
            <small>
              {editAlbum === album.id ? (<button className="btn btn-outline-secondary test" onClick={() => editAlbumData(album.id)}>Speichern</button>) : (<button className="btn btn-outline-secondary test" onClick={() => setEditAlbum(album.id)}>Bearbeiten</button>)}
              <button className="btn btn-outline-secondary" onClick={() => { deleteAlbumData(album.id) }}>Löschen</button>
            </small>
          </div>
          {editAlbum === album.id ? (<input className ="input-group-text mb-2" type='text' placeholder='Künstler' onChange={ e => setEditArtistText(e.target.value)} value={editArtistText}/>) : (<h5 className="mb-1">Künstler: {album.artist}</h5>)}
          {editAlbum === album.id ? (<input className ="input-group-text" type='number' placeholder='' onChange={ e => setEditRelasedYearNumber(e.target.value)} value={editReleasedYearNumber}/>) : (<small className="mb-1">Erscheinungjahr: {album.released_in}</small>)}

        </a>
      </div>
      })}
</div>
</div>
  )
}

export default App
