import React, { useState, useEffect } from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.css';


const App = () => {
  const [albumData, setAlbumData] = useState([])
  const [editAlbum, setEditAlbum] = useState(null)
  const [editText, setEditText] = useState("")

  const deleteAlbumData = id => {
    const newAlbumData = albumData.filter(album => album.id !== id)
      setAlbumData(newAlbumData) 
  }

  const fetchAlbumData =  () => {
     return axios.get('https://hk-test-api.herokuapp.com/albums', {headers: {
      accept: 'application/json',
      'X-API-Key': 'secret'
  }})
    .then((response) => {
        console.log(response.data);
        return response.data
    })
    .catch(e => {
        console.log('error:', e)
    });
  }

  useEffect(() => {
    fetchAlbumData().then( albums =>{
      setAlbumData(albums)
    })
  }, [])

  const editAlbums = (id) => {
    const updatedAlbum = [...albumData].map((album) => {
      if(album.id === id) {
        album.title = editText
      }
      return album
    })
    setAlbumData(updatedAlbum)
    setEditText("")
    setEditAlbum(null)
  }



  return (
    <div>
      {albumData.map( album => {
        return <div className="list-group">
        <a href="#" className="list-group-item list-group-item-action flex-column align-items-start">
          <div className="d-flex w-100 justify-content-between">
            <section>
            <h5 className="mb-3">Titel:</h5> {editAlbum === album.id ? (<input type='text' placeholder='Titel: ' onChange={ e => setEditText(e.target.value)} value={editText}/>) : (<h5 className="mb-1">{album.title}</h5>)}
            </section>
            <small>
              <button className="btn btn-primary test" onClick={() => { deleteAlbumData(album.id) }}>Delete</button>
              <button onClick={() => setEditAlbum(album.id)}>Bearbeiten</button>
              <button onClick={() => editAlbums(album.id)}>Speichern</button>
            </small>
          </div>

          <p className="mb-1">KÃ¼nstler: {album.artist}</p>
          <small>Erscheinungjahr: {album.released_in}</small>

        </a>
      </div>
      })}
</div>
  )
}

export default App
