import React, { useState, useEffect } from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.css';


const App = () => {
  const [albumData, setAlbumData] = useState([])
  const [editAlbum, setEditAlbum] = useState(null)
  const [editTitleText, setEditTitleText] = useState("")
  const [editArtistText, setEditArtistText] = useState("")
  const [editReleasedYearNumber, setEditRelasedYearNumber] = useState()

  const deleteAlbumData = id => {
    const newAlbumData = albumData.filter(album => album.id !== id)
      setAlbumData(newAlbumData) 
  }

  const fetchAlbumData =  () => {
     return axios.get('https://hk-test-api.herokuapp.com/albums', {headers: {
      accept: 'application/json',
      'X-API-Key': 'secret'
  }})
    .then((response) => {
        console.log(response.data);
        return response.data
    })
    .catch(e => {
        console.log('error:', e)
    });
  }

  useEffect(() => {
    fetchAlbumData().then( albums =>{
      setAlbumData(albums)
    })
  }, [])

  const editAlbumData = (id) => {
    const updatedAlbum = [...albumData].map((album) => {
      if(album.id === id) {
        album.title = editTitleText
        album.artist = editArtistText
      }
      return album
    })
    setAlbumData(updatedAlbum)
    setEditTitleText("")
    setEditAlbum(null)
  }



  return (
    <div>
      {albumData.map( album => {
        return <div className="list-group">
        <a href="#" className="list-group-item list-group-item-action flex-column align-items-start">
          <div className="d-flex w-100 justify-content-between">
            {editAlbum === album.id ? (<input type='text' placeholder='Titel: ' onChange={ e => setEditTitleText(e.target.value)} value={editTitleText}/>) : (<h5 className="col-4 mb-1">Titel: {album.title}</h5>)}
            <small>
              <button className="btn btn-primary test" onClick={() => { deleteAlbumData(album.id) }}>Delete</button>
              {editAlbum === album.id ? (<button onClick={() => editAlbumData(album.id)}>Speichern</button>) : (<button onClick={() => setEditAlbum(album.id)}>Bearbeiten</button>)}
            </small>
          </div>
          {editAlbum === album.id ? (<input type='text' placeholder='Titel: ' onChange={ e => setEditArtistText(e.target.value)} value={editArtistText}/>) : (<h5 className="col-4 mb-1">Künstler: {album.artist}</h5>)}
          <p className="mb-1">Künstler: {album.artist}</p>
          <small>Erscheinungjahr: {album.released_in}</small>

        </a>
      </div>
      })}
</div>
  )
}

export default App
